INSERT INTO `szok`.`filmy` (`id`, `tytul`, `opis`, `dataPremiery`, `czasTrwania`, `czasReklam`, `plakat`, `zwiastun`, `KategorieWiekowe_id`) VALUES
(1, 'Avatar', 'Jake, sparaliżowany były komandos, zostaje wysłany na planetę Pandora, gdzie zaprzyjaźnia się z lokalną społecznością i postanawia jej pomóc.', '2009-12-25', 162, 24, 'a06900b3b37ad8c72bbb8ca0762e0728.jpeg', 'https://www.youtube.com/watch?v=5PSNL1qE6VY', 4),
(2, 'Pianista', 'Podczas drugiej wojny światowej Władysław Szpilman, znakomity polski pianista, stara się przeżyć w okupowanej Warszawie.', '2002-05-24', 111, 23, 'b4141e2bcc7710b7569eb7e7ed5f7293.jpeg', 'https://www.youtube.com/watch?v=u_jE7-6Uv7E', 4),
(3, 'Constantine', 'Adaptacja komiksu \"Hellblazer\" opowiadająca historię Johna Constantine\'a - egzorcysty, który musi stanąć do walki o dusze wszystkich ludzi, by tym samym ocalić siebie od wiecznego potępienia.', '2005-02-08', 121, 23, '23ee93f39c676468c11e26de3d404903.jpeg', NULL, 4),
(4, 'Harry Potter i Książę Półkrwi', 'Harry, by ostatecznie pokonać Voldemorta, musi poznać jego przeszłość.', '2009-07-06', 153, 24, 'cc900b20458cbc7250bd3e4057db70d9.jpeg', NULL, 3),
(5, 'Harry Potter i Zakon Feniksa', 'By przygotować się do starcia z Voldemortem praktykując obronę przed czarną magią, Harry wraz z przyjaciółmi zakładają tajne stowarzyszenie - Gwardię Dumbledore\'a.', '2007-06-28', 138, 21, '7d2be4b661d0ddcda1bec31b7499ad79.jpeg', NULL, 3),
(6, 'Legion samobójców', 'Grupa złoczyńców otrzymuje od rządu szansę na odkupienie, wypełniając misję zagrażającą ich życiu.', '2016-07-05', 191, 22, 'c7b3f73222d23fa858cd4fbec24329f6.jpeg', 'https://www.youtube.com/watch?v=XW0raF4HdtE', 4),
(7, 'Raport mniejszości', 'W 2054 roku można przewidzieć przyszłość, a winnych ukarać, zanim popełnią zbrodnię. Komisarz John Anderton zostaje oskarżony o morderstwo, do którego jeszcze nie doszło.', '2002-06-12', 165, 24, '41762082bc7797004267460e2866626b.jpeg', NULL, 4),
(8, 'Prawo zemsty', 'Kiedy żona i córka Clyde\'a Sheltona zostają brutalnie zamordowane, a ich zabójca unika kary, bohater przygotowuje plan zemsty nie tylko na oprawcach, ale i wymiarze sprawiedliwości.', '2007-01-08', 175, 23, 'b82f29b70bead412aa7b70f813a88380.jpeg', NULL, 4),
(9, 'Witaj w klubie', 'Rok 1985, Dallas. Elektryk oraz kanciarz, Ron Woodroof, pracuje nad systemem pomocy w zdobywaniu leków osobom chorym na AIDS.', '2013-09-07', 151, 21, 'cec14281183d107de4ac9e97d6f3b47c.jpeg', NULL, 2),
(10, 'Gwiazd naszych wina', 'Cierpiąca na raka tarczycy Hazel za namową rodziców idzie na spotkanie grupy wsparcia. Poznaje tam nastoletniego Gusa, byłego koszykarza z amputowaną nogą.', '2014-05-16', 188, 25, '4c1b2c711f6c1f8148eefd928b7bc704.jpeg', NULL, 4),
(11, 'Wojna światów', 'Ray z dwójką dzieci stara się przeżyć w czasie inwazji kosmitów na Ziemię.', '2007-01-11', 144, 23, 'd82fc0c424b53e691016aedfc6f10d13.jpeg', NULL, 4),
(12, 'Rzeka tajemnic', 'Adaptacja powieści Dennisa Lehane\'a. Życie trzech przyjaciół zmienia się, gdy jeden z nich wsiada do niewłaściwego samochodu. Po 25 latach ich drogi znowu się krzyżują.', '2003-05-23', 137, 21, 'fcec63bb6214ddb005ef1baa7ad449ea.jpeg', NULL, 3),
(13, 'Ostatni samuraj', 'Weteran wojny secesyjnej przypływa do Japonii pod koniec 1870 roku, aby szkolić żołnierzy cesarza Mutsuhito.', '2003-11-22', 137, 21, '7f097f73440fd6c6ddb0fc97ddb37411.jpeg', NULL, 4),
(14, 'Jak wytresować smoka', 'Chuderlawy Wiking przewróci porządek w wiosce, kiedy zamiast zabić w ramach inicjacji jakiegoś smoka, zaprzyjaźni się z najgroźniejszym z nich.', '2007-01-14', 182, 25, '71f00574f7691efdb4ee1754aea4ede0.jpeg', NULL, 1),
(15, 'Gra tajemnic', 'Angielski matematyk i logik, Alan Turing, pomaga złamać kod Enigmy podczas II wojny światowej.', '2014-08-29', 113, 25, '3155483a108a9c9b41132fdd50caea39.jpeg', NULL, 4),
(16, 'WALL·E', 'Daleka przyszłość. Ziemię opuszczoną przez ludzi próbuje uprzątnąć uroczy robot WALL.E. Pewnego dnia na planetę przybywa EVE – przepiękna, nowoczesna maszyna, która szuka oznak życia.', '2008-06-23', 108, 22, 'd8974216d271bbf7aad0e673dc895c36.jpeg', NULL, 1),
(17, 'Kac Vegas w Bangkoku', 'Kontynuacja \"Kac Vegas\". Czterech przyjaciół udaje się w podróż do egzotycznej Tajlandii na ślub jednego z nich. Spokojny wieczór kawalerski wymyka się spod kontroli.', '2007-01-17', 194, 23, '9233b34ce28a9553093ac2c14bcc5332.jpeg', 'https://www.youtube.com/watch?v=7hzMLRQjSk4', 2),
(18, 'Igrzyska śmierci: W pierścieniu ognia', 'Katniss i Peeta odbywają obowiązkowe Tournée Zwycięzców. Dowiadują się o fali zamieszek, do których doprowadzili, łamiąc reguły igrzysk.', '2013-11-11', 127, 21, '1db1c9c1b9318153ad4d46e9a80feb41.jpeg', NULL, 4),
(19, 'Nienawistna ósemka', 'Dwaj łowcy głów, próbując znaleźć schronienie przed zamiecią śnieżną, trafiają do Wyoming, gdzie wplątani zostają w splot krwawych wydarzeń.', '2015-12-07', 164, 25, '137774c483cdda50add93090f6c561b0.jpeg', NULL, 4),
(20, 'Czerwony smok', 'Kiedy w sprawie psychopatycznego zabójcy policja okazuje się bezradna, agent FBI prosi o pomoc pacjenta więziennego centrum psychiatrycznego, skazanego na dożywocie za wielokrotne morderstwa i kanibalizm.', '2002-09-30', 124, 25, NULL, NULL, 4),
(21, 'Snajper', 'Historia kariery wojskowej i życia osobistego Chrisa Kyle\'a, który zyskał sławę najlepszego snajpera w historii elitarnej jednostki Navy SEALs.', '2014-11-11', 118, 25, '25c9f857baa540bcab89cde9373a66a2.jpeg', NULL, 4),
(22, 'Wielki Gatsby', 'Nowy Jork, rok 1922. Milioner Jay Gatsby spotyka po pięciu latach swoją wielką miłość, Daisy, z którą wcześniej rozdzieliły go koleje losu.', '2013-05-01', 190, 24, '1ba4b5a11e18eb53b12df6b705c9bdbd.jpeg', NULL, 4),
(23, 'Piraci z Karaibów: Na nieznanych wodach', 'Kapitan Jack Sparrow poszukuje słynnej Fontanny Młodości. Na swojej drodze spotyka piękną Angelicę i okrutnego Czarnobrodego.', '2011-05-07', 137, 24, 'f94e84df625e16cf1730d245facd0a39.jpeg', NULL, 3),
(24, 'Sherlock Holmes: Gra cieni', 'Druga odsłona przygód genialnego detektywa Sherlocka Holmesa i jego przyjaciela dra Johna Watsona. Tym razem bohaterowie są na tropie profesora Jamesa Moriarty\'ego.', '2011-12-10', 180, 25, 'f82bfb827b799278413d5947bffc0d41.jpeg', NULL, 4),
(25, 'Labirynt fauna', 'Hiszpania, 1944 rok. Jedenastoletnia Ofelia przenosi się z matką do domu okrutnego ojczyma. Pewnego dnia odkrywa tajemniczy labirynt, w którym spotyka fauna.', '2006-05-27', 112, 25, 'ab22af1816a00a3091c5768c5c8ecf4c.jpeg', NULL, 4),
(26, 'Marsjanin', 'Po nieudanej ekspedycji Mark zostaje sam na Marsie. Mimo znikomych zapasów oraz zerwanej łączności z dowództwem mężczyzna stara się przetrwać w trudnych warunkach.', '2015-09-11', 141, 25, '4bd47638e1c3a8a378e6fbb767c92eba.jpeg', NULL, 4),
(27, 'Babel', 'Trzy historie, rozgrywające się w różnych krajach, połączone motywem problemów z komunikacją.', '2006-05-23', 165, 25, '1af41f46690de590b80dc468e27bea72.jpeg', NULL, 4),
(28, 'Teoria wszystkiego', 'Film opowiada historię genialnego naukowca Stephena Hawkinga - rozwoju jego choroby, rodzącego się uczucia do Jane, a w końcu rozpadu ich małżeństwa.', '2014-09-07', 154, 23, '54974a946c3d1269cb4630cbcdd0823f.jpeg', NULL, 3),
(29, 'Avengers', 'Grupa superbohaterów, na czele z Thorem, Iron Manem i Hulkiem, łączy siły, by obronić Ziemię przed inwazją kosmitów.', '2012-04-11', 142, 20, 'ffb543fbef4b0e748c12d3f63c7f9998.jpeg', NULL, 3),
(30, 'Harry Potter i Insygnia Śmierci: Część I', 'Harry, Ron i Hermiona wyruszają odnaleźć horkruksy, dzięki którym Voldemort zapewnił sobie nieśmiertelność. Muszą je wszystkie zniszczyć, by go pokonać.', '2010-11-11', 146, 20, '6e0bc8fde6d2fc5977f574a8559a8718.jpeg', NULL, 4),
(31, 'Harry Potter i Insygnia Śmierci: Część II', 'Młody czarodziej musi zniszczyć wszystkie Horkruksy, aby ostatecznie pokonać Voldemorta.', '2011-07-07', 138, 25, '7f04352747b7e80c521991c4a39d339d.jpeg', NULL, 4),
(32, 'Przełęcz ocalonych', 'Schyłek II wojny światowej. Podczas krwawej bitwy o Okinawę amerykański sanitariusz odmawia noszenia broni i zabijania z powodów moralnych.', '2016-09-04', 139, 22, '11a52ad39a6fb83b06b1b340eb661f59.jpeg', 'https://www.youtube.com/watch?v=lTqe2ajkYfo', 4),
(33, 'Troja', 'Ekranizacja epopei Homera opowiadająca o walce Greków pod murami Troi, aby odbić porwaną królową Sparty.', '2007-02-02', 177, 20, 'c36b7aaa0a97f3f8026a8cdde295e53f.jpeg', NULL, 3),
(34, 'Mroczny Rycerz powstaje', 'Po ośmiu latach nieobecności Batman wraca, by uratować Gotham City przed zamaskowanym terrorystą Bane\'em.', '2012-07-16', 165, 23, '010b821093ff99c25be8d26301418186.jpeg', NULL, 2),
(35, 'Charlie i fabryka czekolady', 'Ubogi Charlie Bucket znajduje jeden z pięciu złotych biletów, który umożliwia mu zwiedzenie ogromnej fabryki czekolady.', '2005-07-10', 115, 22, 'b754fd949694aa2b9e48fe66787ee7d0.jpeg', NULL, 2),
(36, 'Labirynt', 'Sześcioletnia córka Kellera Dovera i jej koleżanka zostają uprowadzone. Zirytowany bezradnością policji ojciec bierze sprawy w swoje ręce.', '2013-08-30', 153, 23, '94a79ab140364b1458cceb2a5a6b7c13.jpeg', 'https://www.youtube.com/watch?v=qPyLwg1nVH0', 2),
(37, 'Symetria', 'Młody mężczyzna zostaje niesłusznie skazany za rzekomą napaść na staruszkę. W więzieniu stara się dołączyć do grupy grypsujących.', '2003-09-17', 133, 22, '5c0c824a05d692640761561b0cd5ad79.jpeg', NULL, 4),
(38, 'Sin City - Miasto grzechu', 'Trzy historie, których akcja rozgrywa się w Basin City - Mieście Grzechu - będącym siedliskiem prostytutek, przestępców i skorumpowanych obrońców prawa.', '2005-03-28', 159, 23, '8c7d1ade9bb4f2c43f2e9bac28f030f4.jpeg', NULL, 4),
(39, 'Hobbit: Pustkowie Smauga', 'Hobbit Bilbo Baggins razem z Gandalfem oraz trzynastoma krasnoludami zmierza do legowiska smoka Smauga. Bohaterowie chcą pokonać bestię i odebrać jej złoto, które ukradła.', '2013-12-02', 153, 22, '486aefcb1a6f059b57f99cd29b5656c6.jpeg', NULL, 3),
(40, 'Choć goni nas czas', 'Historia dwóch umierających mężczyzn, którzy pod wpływem szalonych przygód zostają przyjaciółmi.', '2007-12-16', 97, 21, '65f16e33a95f01efacf1aa1e2ff4713e.jpeg', NULL, 4),
(41, 'Iron Man', 'Tony Stark buduje supernowoczesną zbroję. Multimiliarder postanawia walczyć ze złem jako Iron Man.', '2008-04-14', 126, 20, '4b9bda63fec97625c7a05fd4d267eb0a.jpeg', 'https://www.youtube.com/watch?v=8hYlB38asDY', 3),
(42, 'Poradnik pozytywnego myślenia', 'Pat Solitano, po tym jak stracił pracę, dom oraz żonę, spędził w szpitalu psychiatrycznym kilka miesięcy. Wrócić do społeczeństwa pomagają mu rodzice, przyjaciele i piękna dziewczyna.', '2007-02-11', 122, 20, 'bd072128020827eb463c6bf5d3aebb3c.jpeg', NULL, 4),
(43, 'To nie jest kraj dla starych ludzi', 'Llewelyn Moss znajduje niedaleko granicy z Meksykiem paczkę heroiny, dwa miliony dolarów i wielu zabitych. Zabierając ze sobą gotówkę, nie wie, że jego śladem podąża psychopatyczny morderca.', '2007-05-19', 112, 21, NULL, NULL, 4),
(44, 'Batman - Początek', 'Historia milionera Bruce\'a Wayne\'a, który przemienia się w Batmana – postrach przestępców Gotham City.', '2005-05-31', 140, 20, '18f6df5aff335ea30fba959373a1a6d5.jpeg', NULL, 4),
(45, 'Igrzyska śmierci', 'W państwie Panem co roku odbywają się Głodowe Igrzyska - walka na śmierć i życie między przedstawicielami dwunastu Dystryktów. Gdy młodsza siostra Katniss zostaje wylosowana do zawodów, dziewczyna bez wahania zajmuje jej miejsce.', '2012-03-12', 144, 24, '2679eed61259b17bc133d1fb43054f70.jpeg', 'https://www.youtube.com/watch?v=TTvi9xco3Cw', 5),
(46, 'Zjawa', 'Hugh Glass szuka zemsty na ludziach, którzy zostawili go poważnie rannego po ataku niedźwiedzia.', '2015-12-16', 119, 25, '6209c36144c4fb70e0b04a119a63fabc.jpeg', NULL, 4),
(47, 'Madagaskar', 'Lew, zebra, hipopotam i żyrafa zostają wysłane statkiem z nowojorskiego zoo do Afryki. Pośrodku oceanu ich klatki wypadają za burtę pokładu, w wyniku czego zwierzęta lądują na brzegu egzotycznej wyspy.', '2005-05-25', 159, 25, '723e473633109343217fe464c10c3f17.jpeg', 'https://www.youtube.com/watch?v=tKijVgFTPX0', 1),
(48, '1408', 'Adaptacja opowiadania Stephena Kinga. Mike Enslin, autor książek o zjawiskach paranormalnych, próbuje podważyć mit pokoju nr 1408.', '2007-06-12', 112, 25, '6833817ea7e9577a2d3a111bb916f1fb.jpeg', NULL, 3),
(49, 'Iluzja', 'Agenci FBI i Interpolu śledzą grupę iluzjonistów, którzy podczas przedstawień napadają na banki, a ukradzione pieniądze rozdają publiczności.', '2013-05-21', 174, 21, NULL, NULL, 3),
(50, 'Deadpool', 'Były żołnierz oddziałów specjalnych zostaje poddany niebezpiecznemu eksperymentowi. Niebawem uwalnia swoje alter ego i rozpoczyna polowanie na człowieka, który niemal zniszczył jego życie.', '2016-06-21', 180, 23, 'd72cdfd85530ea4791d8bf5c6770c031.jpeg', 'https://www.youtube.com/watch?v=CnYv2yj2D3M', 4),
(51, 'Harry Potter i Czara Ognia', 'Harry Potter musi wziąć udział w Turnieju Trójmagicznym, kiedy jego nazwisko zostaje wybrane przez tajemniczą Czarę Ognia.', '2007-02-20', 168, 22, '00ede9093d434c0642376b444a089be3.jpeg', 'https://www.youtube.com/watch?v=YEJw2AvIro8', 5),
(52, 'Harry Potter i więzień Azkabanu', 'Z więzienia ucieka Syriusz Black, Harry nie może już czuć się bezpiecznie w szkole.', '2004-05-13', 177, 23, '67ee2aa2d803642e94f8bd4f6b4858ca.jpeg', 'https://www.youtube.com/watch?v=BV2RLkfYSIM', 4),
(53, 'Służące', 'Lata 60. XX wieku. Początkująca dziennikarka spisuje historie czarnoskórych służących wywołując skandal wśród wyższych sfer.', '2007-02-22', 146, 24, 'f9362892144a726cf6a7f612b4d9d25b.jpeg', 'https://www.youtube.com/watch?v=HFMVDlrLdxc', 4),
(54, 'Piraci z Karaibów: Na krańcu świata', 'Will Turner i Elizabeth Swan wyruszają w podróż na kraniec świata, by uratować kapitana Jacka Sparrowa z pułapki Davy\'ego Jonesa.', '2007-05-19', 139, 22, '966f75dcbccaf704085f54f3e6dab2b5.jpeg', 'https://www.youtube.com/watch?v=N3_iKm_MCVA', 5),
(55, 'Uprowadzona', 'Bryan, były agent służb specjalnych, rusza do Paryża, by ratować swoją 17-letnią córkę porwaną przez gang handlujący kobietami.', '2008-02-27', 93, 20, '549a50e63e29ac42f13d06e3640fe2e8.jpeg', NULL, 4),
(56, 'Kill Bill', 'Płatna zabójczyni zwana Czarną Mambą po czterech latach wybudza się ze śpiączki z zamiarem krwawej zemsty na ludziach, którzy próbowali ją zabić.', '2003-09-29', 112, 22, 'ad1f52fc24807296a340aa00a52f8925.jpeg', NULL, 5),
(57, 'Złap mnie, jeśli potrafisz', 'Oparta na faktach historia młodego fałszerza, który w latach 60., podając się za pilotów, lekarzy i profesorów, wyłudził z banków ponad 2,5 mln dolarów.', '2002-12-16', 183, 20, NULL, NULL, 1),
(58, 'Jestem Bogiem', 'Eddie Morra jest niespełnionym, pogrążonym w depresji pisarzem. Eksperymentalny narkotyk, NZT, powoduje, iż staje się on geniuszem i człowiekiem sukcesu.', '2011-03-08', 105, 20, 'fc756b66947d5af567eea0e6700a6e1e.jpeg', NULL, 4),
(59, 'W pogoni za szczęściem', 'Film oparty na faktach. Chris, samotny ojciec pozbawiony domu, stara się mimo przeciwności losu o posadę w biurze maklerskim.', '2006-12-15', 114, 24, 'a18b23f0b04c38a396c1f49bbe8d3815.jpeg', NULL, 3),
(60, 'Diabeł ubiera się u Prady', 'Początkująca dziennikarka wkracza do świata mody, dostając pracę asystentki redaktor naczelnej czasopisma „Runaway” - Mirandy Priestley, która trzęsie całym światem haute couture.', '2006-06-29', 109, 25, NULL, 'https://www.youtube.com/watch?v=5sfo8p0DMpI', 5),
(61, 'Zaginiona dziewczyna', 'Po przeprowadzce Amy i Nick przeżywają kryzys. Gdy w dniu rocznicy ślubu kobieta znika bez śladu, jej mąż staje się głównym podejrzanym.', '2014-09-26', 184, 25, '67b5ea7f47b52452343ed5d79a284114.jpeg', NULL, 3),
(62, 'Krwawy diament', 'Przemytnik z RPA i rybak z ludu Mende wyruszają na poszukiwanie różowego diamentu, który mógłby zmienić ich życie. Tłem akcji jest wojna domowa w Sierra Leone w latach 90. XX wieku.', '2006-12-08', 143, 20, '2c1e789ab666e883efc1a4f23aa29fa7.jpeg', NULL, 2),
(63, 'Whiplash', 'Młody i ambitny perkusista za wszelką cenę pragnie dołączyć do czołówki najwybitniejszych artystów muzyki jazzowej.', '2014-01-16', 181, 24, '0bf845080c7264b59568efc8574bb50c.jpeg', 'https://www.youtube.com/watch?v=MfXemr222ek', 4),
(64, 'Piraci z Karaibów: Skrzynia umarlaka', 'Jack Sparrow musi spłacić dług zaciągnięty wobec kapitana Latającego Holendra. Uniknie śmierci, gdy znajdzie i zniszczy serce Davy\'ego Jonesa ukryte w Skrzyni Umarlaka.', '2006-06-24', 150, 24, '313cd00c10ec51d54a5a9902908cb873.jpeg', NULL, 3),
(65, 'Alicja w Krainie Czarów', 'Dziewiętnastoletnia Alicja wraca do krainy z dzieciństwa, gdzie dowiaduje się, że jej przeznaczeniem jest zdetronizować Królową Kier.', '2007-03-06', 98, 24, 'bc504ac3765c101f6479280b1f40a6b4.jpeg', 'https://www.youtube.com/watch?v=FFfUEV9LVeQ', 2),
(66, 'Jak zostać królem', 'Jest rok 1939. Jąkający się król Jerzy VI nie może przemawiać do ludu. Pomocy szuka u niekonwencjonalnego specjalisty od dykcji.', '2007-03-07', 151, 24, 'b16d8e8922ac43527b0372968ac2949c.jpeg', NULL, 3),
(67, 'V jak Vendetta', 'W totalitarnej Anglii jedyną osobą walczącą o wolność jest bojownik przebrany za Guya Fawkesa – V, który pewnego dnia uwalnia z rąk agentów rządowych młodą kobietę.', '2005-02-13', 132, 23, 'ac866d4b6aca4ed1749186d6eedb248d.jpeg', NULL, 4),
